const mongoose = require("mongoose");

function setInstance(instances, newInstance) {
  if (
    !(
      newInstance instanceof mongoose.Mongoose ||
      newInstance instanceof mongoose.Connection
    )
  )
    throw new TypeError(
      "Invalid type of instance given, expecting mongoose or connection"
    );

  instances.models = new Map();
  instances.instance = newInstance;
}

module.exports = setInstance;
